/**
     * Checks if the given type literal is not specified in the given configuration exception.
     * 
     * @param typeLiteral the type literal to check
     * @param ce the configuration exception to check
     * @return true if the type literal is not specified in the configuration exception, false otherwise
     */
    private boolean isTypeNotSpecified(TypeLiteral<?> typeLiteral, ConfigurationException ce) {
        Collection<Message> messages = ce.getErrorMessages();
        if (messages.size() == 1) {
            Message msg = Iterables.getOnlyElement(new Errors().keyNotFullySpecified(typeLiteral).getMessages());
            return msg.getMessage().equals(Iterables.getOnlyElement(messages).getMessage());
        } else {
            return false;
        }
    }

